	org_id,
        id,
        depth,
	name,
        padded_name,
	channel_arch_id,
        last_modified,
        label,
	parent_or_self_label,
	parent_or_self_id,
	end_of_life
)
AS
select * from (
	select	cfp.user_id		as user_id,
		cfp.org_id		as org_id,
		c.id			as id,
		1			as depth,
		c.name			as name,
